targets.expr:
100 * (
  (memory_swap_total{job=~"$job", alias=~"$alias"} - memory_swap_free{job=~"$job", alias=~"$alias"})
  /
  on(alias, job)
  memory_swap_total{job=~"$job", alias=~"$alias"}
)

processes_total{job=~"$job", alias=~"$alias"} - (
  processes_running{job=~"$job", alias=~"$alias"} +
  processes_sleeping{job=~"$job", alias=~"$alias"} +
  processes_zombies{job=~"$job", alias=~"$alias"}
)

100 * (
  memory_swap_free{job=~"$job", alias=~"$alias"} 
  /
  on(alias, job)
  memory_swap_total{job=~"$job", alias=~"$alias"}
)

processes_total{job=~"$job", alias=~"$alias"} - (
  processes_running{job=~"$job", alias=~"$alias"} +
  processes_sleeping{job=~"$job", alias=~"$alias"} +
  processes_zombies{job=~"$job", alias=~"$alias"}
)
